package com.portal.otodom.Models;import com.portal.otodom.Errors.ValidPassword;import com.portal.otodom.Errors.ValidUsername;import javax.validation.constraints.NotEmpty;public class UserDTO {    @ValidUsername    @NotEmpty(message = "Username field cannot be empty")    private String username;    @ValidPassword    @NotEmpty(message = "Password field cannot be empty")    private String password;    private String email;    private String phoneNumber;    private String role;    public UserDTO(String username, String password, String email, String phoneNumber, String role) {        this.username = username;        this.password = password;        this.email = email;        this.phoneNumber = phoneNumber;        this.role = role;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getPhoneNumber() {        return phoneNumber;    }    public void setPhoneNumber(String phoneNumber) {        this.phoneNumber = phoneNumber;    }    public String getRole() {        return role;    }    public void setRole(String role) {        this.role = role;    }}